    T.visible,
    T.dmVisible,
    T.depth,
    T.idMap,
    T.updated,
    I.idImage,
    I.filename,
    I.width AS imageWidth,
    I.height AS imageHeight,
    I.type AS imageType,
    IF(T.ruleLink IS NOT NULL,T.ruleLink,I.ruleLink) AS ruleLink,
    S.idSource,
    S.name AS sourceName,
    S.description AS sourceDescription,
    S.copyright,
    L.idLocation,
    L.name as directory,
    L.depth as treeDepth,
    L.idParent
FROM
    Tile as T
        JOIN
    Image as I ON T.idImage = I.idImage
        JOIN
    Source as S ON I.idSource = S.idSource
        JOIN
    Location as L ON I.idLocation = L.idLocation;

